package day4assignment;

import java.util.Scanner;

public class ScannerFloatExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner sc = new Scanner(System.in);
		
		System.out.println("Enter float value in range 5.5f num1 : ");
		float num1=sc.nextFloat();
		
		System.out.println("Enter float value in range 5f num2 : ");
		float num2=sc.nextFloat();
		
		System.out.println("Float value is num1 : "+num1+"F");
		System.out.println("Float value is num2 :"+num2+"F");
		
		

	}

}
