package day4assignment;

import java.util.Scanner;

public class ScannerLongExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		
		System.out.println("Enter Long value in range 10L num1 : ");
		long num1=sc.nextLong();
		
		System.out.println("Enter Long value in range -10L num2 : ");
		long num2=sc.nextLong();
		
		System.out.println("num1 is Long data type : "+ num1+"L");
		System.out.println("num2 is Long data type : "+ num2+"L");
		
		

	}

}
